contracts/ContractDeployer.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/utils/Create2.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { IContractDeployer, IContractRegistry } from "./interfaces/IContractDeployer.sol";

/**
 * @title Contract Deployer
 * @author Miguel Gomez Carpena
 * @notice To check function documentation, please see IContractDeployer documentation
 */
contract ContractDeployer is IContractDeployer {
  IContractRegistry private defaultRegistry;

  /**
   * @notice Initializes the contract and adds intself as first record
   * @dev The logic address in not needed because is the address(this)
   * @param initRegistry default ContractRegistry contract
   */
  constructor(IContractRegistry initRegistry) {
    defaultRegistry = initRegistry;
  }

  function deployContract(
    IContractRegistry registry,
    bytes memory bytecode,
    bytes memory data,
    bytes32 salt,
    bytes32 name,
    uint16 version
  ) external {
    // set default if empty
    if (address(registry) == address(0)) {
      registry = defaultRegistry;
    }
    address registryAddr = address(registry);
    // check if salt is empty and generate a random salt
    if (salt == bytes32(0)) {
      salt = keccak256(abi.encodePacked(bytecode, block.timestamp));
    }
    // deploy logic/implementation contract
    address logic = Create2.deploy(0, salt, bytes.concat(bytecode, data));

    // calculate sha3 hash of the deployed bytecode
    bytes32 logicCodeHash = keccak256(logic.code);

    // the owner is the msg.sender. Implementation May or may not be ownable
    try Ownable(logic).transferOwnership(msg.sender) {} catch {}

    // check if the same contract has been registeded earlier
    (bool found, ) = registry.getRecord(name, msg.sender, 10000);
    // Register new record on ContractRegistry
    if (found) {
      registry.update(name, logic, logic, msg.sender, version, logicCodeHash, msg.sender);
    } else {
      registry.register(name, logic, logic, version, logicCodeHash, msg.sender);
    }
    emit ContractDeployed(registryAddr, name, logic, version, logicCodeHash);
  }
}

contracts/ContractRegistry.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Create2.sol";
import "./interfaces/IContractRegistry.sol";
import "decentralized-code-trust/contracts/Trustable.sol";

/**
 * @title Contract Registry
 * @author Miguel Gomez Carpena
 * @dev This Smart Contract is in charge of keep a registry of deployed SC
 *      for a given system. To check function documentation, please see IContractRegistry documentation
 */
contract ContractRegistry is IContractRegistry, Ownable, Trustable {
  // ======
  // CONSTANTS
  uint16 private constant MAX_VERSION = 9999;
  bytes32 private constant REGISTRY_NAME = bytes32("ContractRegistry");

  // ======
  // VARIABLES
  // Relation to be able to find Record by Name related to an admin
  // --   ((admin,           name)              version) --> proxy
  mapping(address => mapping(bytes32 => mapping(uint16 => ContractRecord))) private contractRecords;
  // Relation to link metadata with an admin
  // --   admin  --> metadata
  mapping(address => Metadata) private metadata;

  // =========
  // FUNCTIONS
  /**
   * @notice Initializes the contract and adds intself as first record
   * @dev The logic address in not needed because is the address(this)
   * @param initCodeTrust the initial code trust to be used
   * @param name (optional) [ContractRegistry] name to identify this contract
   * @param version (optional) [00.00] initial version of the contract
   * @param logicCodeHash the external bytecode or deployBytecode or off-chain bytecode
   */
  constructor(ICodeTrust initCodeTrust, bytes32 name, uint16 version, bytes32 logicCodeHash) {
    // set contract to ask for trusted code
    _codeTrust = initCodeTrust;
    if (name == bytes32(0)) {
      name = REGISTRY_NAME;
    }
    _register(name, address(this), address(this), version, logicCodeHash, _msgSender());
  }

  function register(
    bytes32 name,
    address proxy,
    address logic,
    uint16 version,
    bytes32 logicCodeHash,
    address admin
  ) external {
    // if admin undefined continue as sender
    if (admin == address(0)) {
      admin = _msgSender();
    }
    // or direct call or trusted undirect call
    require(
      admin == _msgSender() || _codeTrust.isTrustedCode(_msgSender(), admin, 0),
      "Call from untrusted address"
    );
    _register(name, proxy, logic, version, logicCodeHash, admin);
  }

  function update(
    bytes32 name,
    address proxy,
    address logic,
    address newAdmin,
    uint16 version,
    bytes32 logicCodeHash,
    address admin
  ) external {
    // if admin undefined continue as sender
    if (admin == address(0)) {
      admin = _msgSender();
    }
    // or direct call or trusted undirect call
    require(
      admin == _msgSender() || _codeTrust.isTrustedCode(_msgSender(), admin, 0),
      "Call from untrusted address"
    );
    _update(name, proxy, logic, newAdmin, version, logicCodeHash, admin);
  }

  function changeRegisteredAdmin(bytes32 name, address newAdmin) external {
    address sender = _msgSender();
    require(newAdmin != address(0) && newAdmin != sender, "Invalid new admin");
    // get data to change admin
    Metadata storage oldMetadata = metadata[sender];
    ContractRecord storage oldRecord = contractRecords[sender][name][
      oldMetadata.latestVersion[name]
    ];
    require(oldRecord.timestamp != 0, "Contract record not registered");
    // store replicated data as newAdmin
    _register(
      oldRecord.name,
      oldRecord.proxy,
      oldRecord.logic,
      oldRecord.version,
      oldRecord.logicCodeHash,
      newAdmin
    );
    // remove prevoious reference
    _removePreviousReference(oldMetadata, name);
    // remove latest version reference
    delete contractRecords[sender][name][oldMetadata.latestVersion[name]];
    emit AdminChanged(name, sender, newAdmin);
  }

  function editExtraData(bytes32 name, bytes memory newExtraData) external {
    address sender = _msgSender();
    // Parameter checks
    // -- keccak256(name) is used as Record ID
    require(name != bytes32(0), "Record name needed");
    // Get sender's metadata
    Metadata storage senderMetadata = metadata[sender];
    // Get record's latestVersion (actualRecord)
    ContractRecord storage actualRecord = contractRecords[sender][name][
      senderMetadata.latestVersion[name]
    ];
    require(actualRecord.timestamp != 0, "Not registered, use register");
    // All OK
    emit ExtraDataUpdated(actualRecord.name, actualRecord.extraData, newExtraData);
    actualRecord.extraData = newExtraData;
  }

  function getRecord(
    bytes32 name,
    address admin,
    uint16 version
  ) external view returns (bool found, ContractRecord memory record) {
    return _getRecord(name, admin, version);
  }

  function getSystemRecords() external view returns (bytes32[] memory latestRecords) {
    return _getRecords(owner());
  }

  function getMyRecords() external view returns (bytes32[] memory latestRecords) {
    return _getRecords(_msgSender());
  }

  function _register(
    bytes32 name,
    address proxy,
    address logic,
    uint16 version,
    bytes32 logicCodeHash,
    address sender
  ) internal {
    // Parameter checks
    require(logic != address(0), "Logic address is nedded");
    // -- regular deployment proxy = logic
    if (proxy == address(0)) {
      proxy = logic;
    }
    require(version <= MAX_VERSION, "Version must be lower than 9999");
    // Other checks
    // get sender's metadata
    Metadata storage senderMetadata = metadata[sender];
    // retrieve the empty contract record
    ContractRecord storage record = contractRecords[sender][name][
      senderMetadata.latestVersion[name]
    ];
    require(record.timestamp == 0 && record.version == 0, "Already registered, use update");
    // get the record qith the param version
    record = contractRecords[sender][name][version];
    // Register record in storage
    record.name = name;
    record.proxy = proxy;
    record.logic = logic;
    record.admin = sender;
    record.version = version;
    record.logicCodeHash = logicCodeHash;
    record.timestamp = block.timestamp;
    // update metadata
    senderMetadata.previousName[name] = senderMetadata.latestRecord;
    senderMetadata.latestRecord = name;
    senderMetadata.latestVersion[name] = version;
    // events
    emit NewRecord(record.name, record.proxy, record.logic, record.version, record.logicCodeHash);
  }

  function _update(
    bytes32 name,
    address proxy,
    address logic,
    address newAdmin,
    uint16 version,
    bytes32 logicCodeHash,
    address sender
  ) internal {
    // Parameter checks
    // -- keccak256(name) is used as Record ID
    require(name != bytes32(0), "Record name needed");
    // Get sender's metadata
    Metadata storage senderMetadata = metadata[sender];
    // Get record's latestVersion (actualRecord)
    ContractRecord storage actualRecord = contractRecords[sender][name][
      senderMetadata.latestVersion[name]
    ];
    require(actualRecord.timestamp != 0, "Not registered, use register");
    // Check that new version is greater than the actual version
    if (version == 0) {
      version = actualRecord.version + 1;
    }
    require(actualRecord.version < version, "Invalid new version");
    ContractRecord storage newRecord = contractRecords[sender][name][version];
    // Register new version
    if (proxy != address(0)) {
      newRecord.proxy = proxy;
    } else {
      newRecord.proxy = actualRecord.proxy;
    }
    if (logic != address(0)) {
      newRecord.logic = logic;
    } else {
      newRecord.logic = actualRecord.logic;
    }
    if (newAdmin != address(0)) {
      newRecord.admin = newAdmin;
    } else {
      newRecord.admin = actualRecord.admin;
    }
    if (logicCodeHash != bytes32(0)) {
      newRecord.logicCodeHash = logicCodeHash;
    } else {
      newRecord.logicCodeHash = actualRecord.logicCodeHash;
    }
    newRecord.name = actualRecord.name;
    newRecord.version = version;
    newRecord.timestamp = block.timestamp;
    senderMetadata.latestVersion[name] = newRecord.version;
    // events
    emit NewRecord(
      newRecord.name,
      newRecord.proxy,
      newRecord.logic,
      newRecord.version,
      newRecord.logicCodeHash
    );
  }

  // previous | actual | next
  function _removePreviousReference(Metadata storage senderMetadata, bytes32 recordName) internal {
    // Get next record reference, wich is the one to change its previous id
    // -- Start from latestRecord and check what is the one whose previousId is the actual recordId
    bytes32 nextName = senderMetadata.latestRecord;
    // if only one record
    if (senderMetadata.previousName[nextName] == bytes32(0)) {
      senderMetadata.previousName[recordName] = bytes32(0);
      senderMetadata.latestRecord = bytes32(0);
    } else {
      while (senderMetadata.previousName[nextName] != recordName) {
        // this means it goes through all the records
        require(nextName != bytes32(0), "RecordName not found for sender");
        nextName = senderMetadata.previousName[nextName];
      }
      // Change the next record's previous id to the actual record's previous id
      senderMetadata.previousName[nextName] = senderMetadata.previousName[recordName];
    }
  }

  function _getRecord(
    bytes32 name,
    address admin,
    uint16 version
  ) internal view returns (bool found, ContractRecord memory record) {
    if (version > MAX_VERSION) {
      version = metadata[admin].latestVersion[name];
    }
    record = contractRecords[admin][name][version];
    if (record.timestamp == 0) {
      found = false;
    } else {
      found = true;
    }
    return (found, record);
  }

  function _getRecords(address admin) internal view returns (bytes32[] memory latestRecords) {
    // first get records length
    bytes32 actualId = metadata[admin].latestRecord;
    uint256 length = 0;
    while (actualId != bytes32(0) && length < 10000) {
      length++;
      actualId = metadata[admin].previousName[actualId];
    }
    // then fill the array
    actualId = metadata[admin].latestRecord;
    bytes32[] memory records = new bytes32[](length);
    for (uint256 i = 0; i < length; i++) {
      records[i] = actualId;
      actualId = metadata[admin].previousName[actualId];
    }
    return records;
  }
}

contracts/interfaces/IContractDeployer.s.content:
//SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.0 <0.9.0;

import { IContractRegistry } from "./IContractRegistry.sol";

/**
 * @title Contract Deployer Interface
 * @author Miguel Gomez Carpena
 * @dev Interface for ContractDeployer Smart Contract
 */
interface IContractDeployer {
  // EVENTS
  // Emits an event when a new contract is succesfully deployed
  event ContractDeployed(
    address registry,
    bytes32 indexed name,
    address indexed proxyOrLogic,
    uint16 indexed version,
    bytes32 logicCodeHash
  );

  // =========
  // FUNCTIONS
  /**
   * @notice Deploys a new contract and registers a new ContractRecord in the ContractRegistry
   * @dev It needs a ContractRegistry SC to register de deployment information
   * @dev It uses Create2 function to deploy the contract
   * @param registry ContractRegistry SC to register the newly deployed ContractRecord
   * @param bytecode The complete bytecode of the contract to be deployed
   * @param data ABI Encoded initialize call data
   * @param salt (optional) [0x00...00] see Create2
   * @param name Name to identify this contractRecord
   * @param version (optional) [00.00] Initial version of the contract
   */
  function deployContract(
    IContractRegistry registry,
    bytes calldata bytecode,
    bytes memory data,
    bytes32 salt,
    bytes32 name,
    uint16 version
  ) external;
}

contracts/interfaces/IContractRegistry.s.content:
// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.0 <0.9.0;

/**
 * @title Contract Record
 * @notice It represents a contract record or a deployment of a contract in blockchain
 * @param name unique identifier for the ContractRecord. Bytes32 that should represent a 32 character string
 * @param proxy the adress of the proxy | storage contract. If it isn't a upgradeable deployment, it is equal to logic
 * @param logic the address of the logic | implementation contract
 * @param admin the address of the admin acount. Refers to any entity that is can have any set of ContractRecords
 * @param version the version of the ContractRecord. Range is [0, 9999] and should be represented externally as 103 -> "01.03" for example.
 *                Usually if version comes > 9999, it is set as 'latest'
 * @param logicCodeHash the hash of the deployed bytecode, not the complete bytecode. It should be used to check the deployed code for trust/safety concerns
 * @param timestamp the timestamp of the ContractRecord registeration for each version
 */
struct ContractRecord {
  bytes32 name;
  address proxy;
  address logic;
  address admin;
  uint16 version;
  bytes32 logicCodeHash;
  bytes extraData;
  uint256 timestamp;
}

/**
 * @title Metadata
 * @notice Other information about the contract record storage needed to retrieve the ContractRecords
 * @param latestVersion mapping that stores the latest version for a given ContractRecord
 * @param previousName mapping that stores the previous name for a given ContractRecord
 * @param latestRecord latest ContractRecord that has been registered
 */
struct Metadata {
  mapping(bytes32 => uint16) latestVersion;
  mapping(bytes32 => bytes32) previousName;
  bytes32 latestRecord;
}

/**
 * @title Contract Registry Interface
 * @author Miguel Gomez Carpena
 * @dev Interface for ContractRegistry Smart Contract
 */
interface IContractRegistry {
  // EVENTS
  // should be emitted when a contract record is registered or updated
  event NewRecord(
    bytes32 indexed name,
    address indexed proxy,
    address logic,
    uint16 indexed version,
    bytes32 logicCodeHash
  );
  // should be emitted when a contract record changes it's registered admin
  event AdminChanged(bytes32 indexed name, address indexed oldAdmin, address indexed newAdmin);
  event ExtraDataUpdated(
    bytes32 indexed name,
    bytes indexed oldExtraData,
    bytes indexed newExtraData
  );

  // =========
  // FUNCTIONS

  /**
   * @notice Registers a contract deployed as a new ContractRecord
   * @param name ContractRecord's name
   * @param proxy (optional) [logic] ContractRecord's proxy
   * @param logic ContractRecord's logic
   * @param version (optional) [0] ContractRecord's version
   * @param logicCodeHash ContractRecord's logic code hash
   * @param admin (optional) [sender] Address of the admin to use
   */
  function register(
    bytes32 name,
    address proxy,
    address logic,
    uint16 version,
    bytes32 logicCodeHash,
    address admin
  ) external;

  /**
   * @notice Updates the ContractRecord of a contract deployment. Used only when it is upgradeable
   * @param name ContractRecord's name
   * @param proxy (optional) [logic] ContractRecord's proxy
   * @param logic ContractRecord's logic
   * @param newAdmin (optional) [0x00..00] Change the registered admin
   * @param version (optional) [actual + 1] ContractRecord's version
   * @param logicCodeHash ContractRecord's logic code hash
   * @param admin (optional) [sender] Address of the admin to use
   */
  function update(
    bytes32 name,
    address proxy,
    address logic,
    address newAdmin,
    uint16 version,
    bytes32 logicCodeHash,
    address admin
  ) external;

  /**
   * @notice Changes the registered admin/owner of a Contract Record
   * @param name ContractRecord's name
   * @param newAdmin Address of the new admin
   */
  function changeRegisteredAdmin(bytes32 name, address newAdmin) external;

  /**
   * @notice Edit / replaces a Contract Record's extraData field
   * @param name ContractRecord's name
   * @param newExtraData ContractRecord's new extraData field
   * @dev it emits an ExtraDataUpdated event
   */
  function editExtraData(bytes32 name, bytes calldata newExtraData) external;

  /**
   * @notice Retreives the contract record by a given name and admin
   * @param name ContractRecord's name
   * @param admin ContractRecord's admin
   * @param version ContractRecord's version. Use version > 9999 to retreive the latest one
   * @return found Whether the contract record is found or not
   * @return record Actual contract record struct object
   */
  function getRecord(
    bytes32 name,
    address admin,
    uint16 version
  ) external view returns (bool found, ContractRecord calldata record);

  /**
   * @notice Retreives all contract record's IDs associated to the system or owner of this ContractRegistry
   * @return latestRecords Array of contract names list
   */
  function getSystemRecords() external view returns (bytes32[] calldata latestRecords);

  /**
   * @notice Retreives all contract record's IDs associated to the system or owner of this ContractRegistry
   * @return latestRecords Array of contract names list
   */
  function getMyRecords() external view returns (bytes32[] calldata latestRecords);
}

